def m5hwGetbatchNo(request):
    if request.method == "GET" and request.is_ajax():
        partNo = request.GET.get('partNo')
        msgSet = 'False'
        mob_temp=[]
        bono_Temp = Batch.objects.filter(part_no = partNo).values('bo_no').exclude(part_no__isnull=True).distinct()                  
