        ls=up_dt.split('-')
        dt=ls[2]+"-"+ls[1]+"-"+ls[0]
        ovd_per=request.GET.get('ovd_per')
        if(txtshop==""):
            return JsonResponse({"success":False}, status = 400)
        shop_list=list(Shop.objects.values('shop'))
